const channel = $('Get Channel Data').item.json.items[0];
const videos = $('Get Video Stats').item.json.items || [];

// Analyze video performance
const topVideos = videos
  .filter(v => v.statistics?.viewCount)
  .sort((a, b) => parseInt(b.statistics.viewCount) - parseInt(a.statistics.viewCount))
  .slice(0, 5);

const avgViews = videos.reduce((sum, v) => sum + parseInt(v.statistics?.viewCount || 0), 0) / videos.length;

// Extract common themes from titles
const allTitles = videos.map(v => v.snippet.title).join(' ');
const allDescriptions = videos.map(v => v.snippet.description).join(' ');
const allTags = videos.flatMap(v => v.snippet.tags || []);
const tagFrequency = allTags.reduce((acc, tag) => {
  acc[tag] = (acc[tag] || 0) + 1;
  return acc;
}, {});
const commonTags = Object.entries(tagFrequency)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 20)
  .map(([tag]) => tag);

// Build context object
const context = {
  channel: {
    name: channel.snippet.title,
    description: channel.snippet.description || '',
    keywords: channel.brandingSettings?.channel?.keywords || '',
    subscriberCount: parseInt(channel.statistics?.subscriberCount || 0),
    videoCount: parseInt(channel.statistics?.videoCount || 0),
    viewCount: parseInt(channel.statistics?.viewCount || 0),
    country: channel.snippet.country || 'Not set',
    defaultLanguage: channel.snippet.defaultLanguage || 'en'
  },
  videos: {
    total: videos.length,
    avgViews: Math.round(avgViews),
    topPerformers: topVideos.map(v => ({
      title: v.snippet.title,
      views: parseInt(v.statistics.viewCount),
      likes: parseInt(v.statistics.likeCount || 0),
      comments: parseInt(v.statistics.commentCount || 0),
      tags: v.snippet.tags || []
    })),
    commonTags: commonTags,
    sampleTitles: videos.slice(0, 10).map(v => v.snippet.title),
    avgTitleLength: Math.round(videos.reduce((sum, v) => sum + v.snippet.title.length, 0) / videos.length),
    avgDescLength: Math.round(videos.reduce((sum, v) => sum + (v.snippet.description || '').length, 0) / videos.length)
  }
};

return [{ json: context }];

