const aiResponse =  $input.item.json;
const context = $('Build AI Prompt').item.json.context;

try {
  // AI Agent returns output in different format
  let responseText = '';

  // Check if response is from AI Agent (has output field)
  if (aiResponse.output) {
    responseText = aiResponse.output;
  } else if (aiResponse.text) {
    responseText = aiResponse.text;
  } else {
    responseText = JSON.stringify(aiResponse);
  }

  // Remove markdown code blocks if present
  const jsonText = responseText
    .replace(/```json\n?/g, '')
    .replace(/```\n?/g, '')
    .trim();

  const recommendations = JSON.parse(jsonText);

  return [{
    json: {
      channelName: context.channel.name,
      channelId: $('Get Channel Data').item.json.items[0].id,
      generatedAt: new Date().toISOString(),
      recommendations: recommendations,
      currentSettings: {
        description: context.channel.description,
        keywords: context.channel.keywords
      },
      aiResponseRaw: responseText
    }
  }];

} catch (error) {
  return [{
    json: {
      error: 'Failed to parse AI response',
      errorDetails: error.message,
      rawResponse: aiResponse,
      channelName: context.channel.name
    }
  }];
}

