const channel = $('Get Channel Details').item.json.items[0];
const videos = $('Get Video Details').item.json.items || [];

const audit = {
  channelId: channel.id,
  channelName: channel.snippet.title,
  channelUrl: `https://youtube.com/channel/${channel.id}`,
  auditDate: new Date().toISOString(),

  // Branding Audit
  branding: {
    profileImage: {
      exists: !!channel.snippet.thumbnails?.default?.url,
      url: channel.snippet.thumbnails?.default?.url,
      status: channel.snippet.thumbnails?.default?.url ? 'âœ… Set' : 'âŒ Missing',
      recommendation: channel.snippet.thumbnails?.default?.url ? 'Ensure it\'s high quality (800x800px recommended)' : 'Upload a profile picture immediately - critical for brand recognition'
    },
    banner: {
      exists: !!channel.brandingSettings?.image?.bannerExternalUrl,
      url: channel.brandingSettings?.image?.bannerExternalUrl,
      status: channel.brandingSettings?.image?.bannerExternalUrl ? 'âœ… Set' : 'âŒ Missing',
      recommendation: channel.brandingSettings?.image?.bannerExternalUrl ? 'Verify it displays correctly on all devices (2560x1440px recommended)' : 'Upload a channel banner - highly recommended for professional appearance'
    },
    watermark: {
      exists: !!channel.brandingSettings?.image?.watermarkImageUrl,
      url: channel.brandingSettings?.image?.watermarkImageUrl,
      status: channel.brandingSettings?.image?.watermarkImageUrl ? 'âœ… Set' : 'âš ï¸ Missing',
      recommendation: channel.brandingSettings?.image?.watermarkImageUrl ? 'Good - helps with channel branding' : 'Set a watermark to increase channel recognition and subscriptions'
    }
  },

  // Channel Info Audit
  channelInfo: {
    description: {
      exists: !!channel.snippet.description,
      length: (channel.snippet.description || '').length,
      text: channel.snippet.description || '',
      status: (channel.snippet.description || '').length > 100 ? 'âœ… Good' : (channel.snippet.description || '').length > 0 ? 'âš ï¸ Too Short' : 'âŒ Missing',
      recommendation: (channel.snippet.description || '').length > 100 ? 'Consider adding keywords and social links' : 'Write a detailed channel description (200-1000 chars) with keywords and links'
    },
    customUrl: {
      exists: !!channel.snippet.customUrl,
      url: channel.snippet.customUrl,
      status: channel.snippet.customUrl ? 'âœ… Set' : 'âš ï¸ Not Set',
      recommendation: channel.snippet.customUrl ? 'Good - custom URL is set' : 'Eligible for custom URL after 100 subs and 30 days old'
    },
    country: {
      exists: !!channel.snippet.country,
      code: channel.snippet.country,
      status: channel.snippet.country ? 'âœ… Set' : 'âš ï¸ Not Set',
      recommendation: channel.snippet.country ? 'Good - helps with regional targeting' : 'Set your country for better regional recommendations'
    },
    defaultLanguage: {
      exists: !!channel.snippet.defaultLanguage,
      code: channel.snippet.defaultLanguage,
      status: channel.snippet.defaultLanguage ? 'âœ… Set' : 'âš ï¸ Not Set',
      recommendation: channel.snippet.defaultLanguage ? 'Ensure it matches your primary content language' : 'Set default language to help YouTube recommend to right audience'
    }
  },

  // Channel Settings Audit
  settings: {
    keywords: {
      exists: !!channel.brandingSettings?.channel?.keywords,
      count: (channel.brandingSettings?.channel?.keywords || '').split(',').filter(k => k.trim()).length,
      keywords: channel.brandingSettings?.channel?.keywords || '',
      status: (channel.brandingSettings?.channel?.keywords || '').split(',').filter(k => k.trim()).length >= 5 ? 'âœ… Good' : (channel.brandingSettings?.channel?.keywords || '').length > 0 ? 'âš ï¸ Add More' : 'âŒ Missing',
      recommendation: (channel.brandingSettings?.channel?.keywords || '').split(',').filter(k => k.trim()).length >= 5 ? 'Good - consider updating based on content evolution' : 'Add 10-20 relevant keywords to help with discovery'
    },
    trackingAnalyticsAccountId: {
      exists: !!channel.brandingSettings?.channel?.trackingAnalyticsAccountId,
      status: channel.brandingSettings?.channel?.trackingAnalyticsAccountId ? 'âœ… Set' : 'âš ï¸ Not Set',
      recommendation: 'Consider linking Google Analytics for advanced insights'
    },
    unsubscribedTrailer: {
      exists: !!channel.brandingSettings?.channel?.unsubscribedTrailer,
      videoId: channel.brandingSettings?.channel?.unsubscribedTrailer,
      status: channel.brandingSettings?.channel?.unsubscribedTrailer ? 'âœ… Set' : 'âš ï¸ Not Set',
      recommendation: channel.brandingSettings?.channel?.unsubscribedTrailer ? 'Good - channel trailer is set for new visitors' : 'Create a channel trailer to convert visitors to subscribers'
    },
    featuredChannels: {
      exists: !!channel.brandingSettings?.channel?.featuredChannelsUrls,
      count: (channel.brandingSettings?.channel?.featuredChannelsUrls || []).length,
      status: (channel.brandingSettings?.channel?.featuredChannelsUrls || []).length > 0 ? 'âœ… Set' : 'âš ï¸ Not Set',
      recommendation: 'Feature related channels to build community and get featured back'
    }
  },

  // Video Content Audit
  recentVideos: {
    totalAnalyzed: videos.length,
    avgTitleLength: videos.reduce((sum, v) => sum + (v.snippet?.title || '').length, 0) / videos.length || 0,
    avgDescriptionLength: videos.reduce((sum, v) => sum + (v.snippet?.description || '').length, 0) / videos.length || 0,
    avgTagCount: videos.reduce((sum, v) => sum + (v.snippet?.tags || []).length, 0) / videos.length || 0,
    videosWithoutTags: videos.filter(v => !(v.snippet?.tags || []).length).length,
    videosWithShortDescription: videos.filter(v => (v.snippet?.description || '').length < 100).length,
    videosWithoutThumbnail: videos.filter(v => v.snippet?.thumbnails?.default?.url?.includes('default')).length,

    titleStatus: function() {
      const avg = this.avgTitleLength;
      if (avg >= 40 && avg <= 70) return 'âœ… Good (40-70 chars)';
      if (avg < 40) return 'âš ï¸ Too Short';
      return 'âš ï¸ Too Long';
    }(),

    descriptionStatus: function() {
      const avg = this.avgDescriptionLength;
      if (avg >= 200) return 'âœ… Good (200+ chars)';
      if (avg >= 100) return 'âš ï¸ Could be longer';
      return 'âŒ Too Short';
    }(),

    tagsStatus: function() {
      const avg = this.avgTagCount;
      if (avg >= 5) return 'âœ… Good (5+ tags)';
      if (avg >= 3) return 'âš ï¸ Add more tags';
      return 'âŒ Insufficient tags';
    }(),

    recommendations: [
      videos.filter(v => (v.snippet?.title || '').length < 40).length > 0 ? `${videos.filter(v => (v.snippet?.title || '').length < 40).length} videos have short titles (< 40 chars)` : null,
      videos.filter(v => (v.snippet?.description || '').length < 100).length > 0 ? `${videos.filter(v => (v.snippet?.description || '').length < 100).length} videos have short descriptions (< 100 chars)` : null,
      videos.filter(v => !(v.snippet?.tags || []).length).length > 0 ? `${videos.filter(v => !(v.snippet?.tags || []).length).length} videos have no tags` : null,
    ].filter(r => r)
  },

  // Overall Score
  score: {
    branding: 0,
    channelInfo: 0,
    settings: 0,
    content: 0,
    total: 0
  }
};

// Calculate scores
audit.score.branding = [
  audit.branding.profileImage.exists ? 30 : 0,
  audit.branding.banner.exists ? 25 : 0,
  audit.branding.watermark.exists ? 10 : 0
].reduce((a, b) => a + b, 0);

audit.score.channelInfo = [
  audit.channelInfo.description.length > 100 ? 20 : audit.channelInfo.description.length > 0 ? 10 : 0,
  audit.channelInfo.customUrl.exists ? 10 : 0,
  audit.channelInfo.country.exists ? 5 : 0,
  audit.channelInfo.defaultLanguage.exists ? 5 : 0
].reduce((a, b) => a + b, 0);

audit.score.settings = [
  audit.settings.keywords.count >= 5 ? 15 : audit.settings.keywords.count > 0 ? 7 : 0,
  audit.settings.unsubscribedTrailer.exists ? 10 : 0,
  audit.settings.featuredChannels.count > 0 ? 5 : 0
].reduce((a, b) => a + b, 0);

audit.score.content = [
  audit.recentVideos.avgTitleLength >= 40 && audit.recentVideos.avgTitleLength <= 70 ? 10 : 5,
  audit.recentVideos.avgDescriptionLength >= 200 ? 10 : audit.recentVideos.avgDescriptionLength >= 100 ? 5 : 0,
  audit.recentVideos.avgTagCount >= 5 ? 10 : audit.recentVideos.avgTagCount >= 3 ? 5 : 0
].reduce((a, b) => a + b, 0);

audit.score.total = audit.score.branding + audit.score.channelInfo + audit.score.settings + audit.score.content;

// Priority Actions (top 5 most important)
audit.priorityActions = [];

if (!audit.branding.profileImage.exists) {
  audit.priorityActions.push({ priority: 'CRITICAL', action: 'Upload profile picture', impact: 'High - Essential for brand recognition' });
}
if (!audit.branding.banner.exists) {
  audit.priorityActions.push({ priority: 'HIGH', action: 'Upload channel banner', impact: 'High - Professional appearance' });
}
if (audit.channelInfo.description.length < 100) {
  audit.priorityActions.push({ priority: 'HIGH', action: 'Write detailed channel description', impact: 'High - SEO and user understanding' });
}
if (audit.settings.keywords.count < 5) {
  audit.priorityActions.push({ priority: 'HIGH', action: 'Add channel keywords (10-20)', impact: 'High - Discovery and SEO' });
}
if (!audit.settings.unsubscribedTrailer.exists) {
  audit.priorityActions.push({ priority: 'MEDIUM', action: 'Create channel trailer', impact: 'Medium - Convert visitors to subscribers' });
}
if (!audit.branding.watermark.exists) {
  audit.priorityActions.push({ priority: 'MEDIUM', action: 'Add channel watermark', impact: 'Medium - Branding and subscriptions' });
}
if (audit.recentVideos.videosWithoutTags > 0) {
  audit.priorityActions.push({ priority: 'MEDIUM', action: `Add tags to ${audit.recentVideos.videosWithoutTags} videos`, impact: 'Medium - Video discovery' });
}

return [{ json: audit }];

